import React, { createContext, useReducer } from 'react';

// 5. The reducer - this is used to update the state, based on the action
export const AppReducer = (state, action) => {
    let new_budgets = [];
    switch (action.type) {
        case 'ADD_QUANTITY':
            let updatedqty = false;
            state.budgets.map((budget)=>{
                if(budget.department === action.payload.department) {
                    budget.quantity = budget.quantity + action.payload.quantity;
                    updatedqty = true;
                }
                new_budgets.push(budget);
                return true;
            })
            state.budgets = new_budgets;
            action.type = "DONE";
            return {
                ...state,
            };

            case 'RED_QUANTITY':
                state.budgets.map((budget)=>{
                    if(budget.department === action.payload.department) {
                        budget.quantity = budget.quantity - action.payload.quantity;
                    }
                    budget.quantity = budget.quantity < 0 ? 0: budget.quantity;
                    new_budgets.push(budget);
                    return true;
                })
                state.budgets = new_budgets;
                action.type = "DONE";
                return {
                    ...state,
                };
        case 'DELETE_ITEM':
            state.budgets.map((budget)=>{
                if(budget.department === action.payload.department) {
                    budget.quantity = 0;
                }
                new_budgets.push(budget);
                return true;
            })
            state.budgets = new_budgets;
            action.type = "DONE";
            return {
                ...state,
            };
    case 'CHG_LOCATION':
            action.type = "DONE";
            state.Location = action.payload;
            return {
                ...state
            }

        default:
            return state;
    }
};

// 1. Sets the initial state when the app loads
const initialState = {
    budgets: [
        { id: "IT", department: 'IT', quantity: 0, unitbudget: 500 },
        { id: "Finance", department: 'Finance', quantity: 0, unitbudget: 300 },
        { id: "HR", department: 'HR', quantity: 0, unitbudget: 40 },
        { id: "Marketing", department: 'Marketing', quantity: 0, unitbudget: 50 },
        { id: "Sales", department: 'Sales', quantity: 0, unitbudget: 70 },
    ],
    Location: '£'
};

// 2. Creates the context this is the thing our components import and use to get the state
export const AppContext = createContext();

// 3. Provider component - wraps the components we want to give access to the state
// Accepts the children, which are the nested(wrapped) components
export const AppProvider = (props) => {
    // 4. Sets up the app state. takes a reducer, and an initial state
    const [state, dispatch] = useReducer(AppReducer, initialState);

    const totalbudgets = state.budgets.reduce((total, item) => {
        return (total = total + (item.unitbudget*item.quantity));
    }, 0);
state.CartValue = totalbudgets;

    return (
        <AppContext.Provider
            value={{
                budgets: state.budgets,
                CartValue: state.CartValue,
                dispatch,
                Location: state.Location
            }}
        >
            {props.children}
        </AppContext.Provider>
    );
};
