export const AppReducer = (state, action) => {
    switch (action.type) {
        case 'ADD_QUANTITY':
            return {
                ...state,
                budgets: state.budgets.map((budget) => {
                    if (budget.department === action.payload.department) {
                        return {
                            ...budget,
                            quantity: budget.quantity + action.payload.quantity,
                        };
                    }
                    return budget;
                }),
            };

        case 'RED_QUANTITY':
            return {
                ...state,
                budgets: state.budgets.map((budget) => {
                    if (budget.department === action.payload.department) {
                        let newQuantity = budget.quantity - action.payload.quantity;
                        newQuantity = newQuantity < 0 ? 0 : newQuantity;
                        return {
                            ...budget,
                            quantity: newQuantity,
                        };
                    }
                    return budget;
                }),
            };

        case 'DELETE_ITEM':
            return {
                ...state,
                budgets: state.budgets.map((budget) => {
                    if (budget.department === action.payload.department) {
                        return {
                            ...budget,
                            quantity: 0,
                        };
                    }
                    return budget;
                }),
            };

        case 'CHG_Currency':
            return {
                ...state,
                Currency: action.payload,
            };

        default:
            return state;
    }
};
