import React, { useContext } from 'react';
import ExpenseItem from './Item';
import { AppContext } from '../context/AppContext';

const List = () => {
    const { expenses } = useContext(AppContext);

    // Add a check to ensure expenses is an array before using map
    if (!Array.isArray(expenses)) {
        return (
            <div className='alert alert-danger'>
                Expenses data is not available.
            </div>
        );
    }

    return (
        <table className='table'>
            <thead className="thead-light">
                <tr>
                    <th scope="col">Department</th>
                    <th scope="col">Allocated Budget</th>
                    <th scope="col">Increase by 10</th>
                    <th scope="col">Decrease by 10</th>
                    <th scope="col">Remove</th>
                </tr>
            </thead>
            <tbody>
            {expenses.map((expense) => (
    <ExpenseItem key={expense.id} department={expense.department} quantity={expense.quantity} unitprice={expense.unitprice} />
))}

            </tbody>
        </table>
    );
};

export default List;
